    owner: "uc admins",
    forceDestroy: true,
});
const thisMetastoreAssignment = new databricks.MetastoreAssignment("thisMetastoreAssignment", {
    metastoreId: thisMetastore.id,
    workspaceId: local.workspace_id,
});
