    principal: ds.displayName,
    permission: "READ",
    scope: app.name,
});
const publishingApi = new databricks.Secret("publishingApi", {
    key: "publishing_api",
    stringValue: data.azurerm_key_vault_secret.example.value,
    scope: app.name,
});
