            new Databricks.Inputs.MlflowModelTagArgs
            {
                Key = "key1",
                Value = "value1",
            },
            new Databricks.Inputs.MlflowModelTagArgs
            {
                Key = "key2",
                Value = "value2",
            },
        },
    });

});
```
