		secretKey := "some-sp-secret"
		container := "test"
		storageAcc := "lrs"
		_, err := databricks.NewMount(ctx, "this", &databricks.MountArgs{
			Uri: pulumi.String(fmt.Sprintf("abfss://%v@%v.dfs.core.windows.net", container, storageAcc)),
			ExtraConfigs: pulumi.Map{
